_valmgr_abi = None
_valmgr_ct = None
_valmgr_code = None
_valmgr_bytecode = None
_valmgr_addr = None
_valmgr_sender_addr = None
_valmgr_tx = None

# old one, address=0xCb969cAAad21A78a24083164ffa81604317Ab603
viper_rlp_decoder_tx = rlp.decode(eth_utils.decode_hex(""), SpuriousDragonTransaction)
